css:
.print-format .table-bordered,
.print-format .table-bordered > thead > tr > th,
.print-format .table-bordered > tbody > tr > th,
.print-format .table-bordered > tfoot > tr > th,
.print-format .table-bordered > thead > tr > td,
.print-format .table-bordered > tbody > tr > td,
.print-format .table-bordered > tfoot > tr > td {
	border: 1px solid black;
}
.print-format td, .print-format th {
    vertical-align: top !important;
    padding: 2px !important;
}

.table, th, td {
    border: 1px solid black;
}
 table, tr, td, th {
      page-break-inside: avoid;
    }
 .table {
      width: 100%;
      border-collapse: collapse;
      border: 1px solid black;
    }
.ql-editor {
    font-family: var(--font-stack);
    color: var(--text-color);
    line-height: 0.9;
    overflow-wrap: break-word;
    white-space: pre-wrap;
}
.print-format td, .print-format th {
   padding-right:1px !important;
   padding-left:1px !important;
   padding-bottom: 1px !important;
   padding-top: 1px !important;
}


.print-format .row:not(.section-break) {
    line-height: 1;
    margin-top: 0.5px !important;
}
.print-format .table-bordered th {
    border: 1px solid black;
}



.print-format th {
    color: black;
    font-weight: bold;
    border-bottom-width: 1px !important;
}

.print-format p {
    margin: 1px 3px 1px;
}



.print-heading {
    display: none;
}
 body {



        box-sizing: border-box; /* Ensure padding is included in width/height */
    }




















@page {
    size: A4 portrait;
    margin: 10mm;
    margin-bottom: 10px;
    border:1px solid black
}

.print-heading {
    border-bottom: 2px solid #333;
    display: none;
}
.table > caption + thead > tr:first-child > th, .table > colgroup + thead > tr:first-child > th, .table > thead:first-child > tr:first-child > th, .table > caption + thead > tr:first-child > td, .table > colgroup + thead > tr:first-child > td, .table > thead:first-child > tr:first-child > td {
    border-top: 0;
    text-align: center;
    line-height: 0.9;
    border:1px solid black;
    font-weight:bold;
}
.print-format .table-bordered td, .print-format .table-bordered th {
    border: 1px solid black;
    line-height: 0.9;
}
.print-format .table-bordered th {
    border: 1px solid black;
    line-height: 0.9;
}
.print-format th {
    color: black;
    font-weight: bold;
    line-height:0.9;
    text-align:center;
    border-bottom-width: 1px !important;
}
.print-format table td .value {
    font-size: 12px;
    line-height:0.9;
}
.print-format .row:not(.section-break) {
    line-height: 1.2;
    margin-top: 3px !important;
}
.print-format label {
    font-weight: bold;
    font-size: 13px;
    color:black;
    margin-bottom: 0;
}
.print-format td, .print-format th {
    padding: 5px !important;
}
.print-format .letter-head {
    margin-bottom: 1px;
}
h4, .h4, h5, .h5, h6, .h6 {
    margin-top: 5px;
    margin-bottom: 5px;
}

[data-fieldname="payment_schedule"] .value { text-align: center; }
[data-fieldname="payment_schedule"] .table-sr { text-align: center; }
[data-fieldname="terms"] .ql-snow { padding: 1px; }
